import React,{useState} from "react";
import { useDispatch } from "react-redux";
import { searchFilmAction } from "../../redux/searchFilm/searchAction";
import MovieItem from "../MovieItem/MovieItem";
import "./SearchBox.css";
import { useSelector } from "react-redux";
const SearchBox = () => {
  const [searchLine, setSearchLine] = useState("");
const dispatch=useDispatch();
const films = useSelector((state) => state.searchReducer.film);
  const searchLineChangeHandler = (e) => {
    setSearchLine(e.target.value);
  };
  const searchBoxSubmitHandler = (e) => {
    e.preventDefault();
    fetch(`http://www.omdbapi.com/?s=${searchLine}&apikey=226bff17`)
  .then((res) => res.json())
  .then((data) => {

    dispatch(searchFilmAction(data.film));

  })

  };

  return (
    <div className="search-box">
      <form className="search-box__form" onSubmit={searchBoxSubmitHandler}>
        <label className="search-box__form-label">
          Искать фильм по названию:
          <input
            value={searchLine}
            type="text"
            className="search-box__form-input"
            placeholder="Например, Shawshank Redemption"
            onChange={searchLineChangeHandler}
          />,

        </label>
        <button
          type="submit"
          className="search-box__form-submit"
          disabled={!searchLine}
          onClick={searchBoxSubmitHandler}
        >
          Искать
        </button>
      </form>
      <div className="movies-container">
        {films.map((movie) => (
          <MovieItem
            key={movie.id}
            id={movie.id}
            title={movie.Title}
            year={movie.Year}
            poster={movie.Poster}
          />
        ))}
      </div>
    </div>
  );
};

export default SearchBox;
