import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { createFavoriteList, deleteFilmAction } from "../../redux/addFavorite/favoriteAction";

import "./Favorites.css";

const Favorites = () => {
  const dispatch = useDispatch();

  const listName = useSelector(
    (store) => store.favoriteReducer.favoriteListName
  );
  const favoriteFilms = useSelector(
    (store) => store.favoriteReducer.favoriteFilms
  );

  const deleteFavorite = () => {
    favoriteFilms&& favoriteFilms.map((film) => (
    dispatch(deleteFilmAction(film))))
  };
  const updateListName = (e) => {
    dispatch(createFavoriteList(e.target.value));
  };


  return (
    <div className="favorites">
      <input
        value={listName}
        className="favorites__name"
        placeholder="Новый список"
        onChange={updateListName}
      />
      <ul className="favorites__list">
        {favoriteFilms?.map((item) => {
          return (
            <li key={item.imdbID}>
              {item.Title} ({item.Year}){" "}
              <button className="favorites__button" onClick={deleteFavorite}>X</button>
            </li>
          );
        })}
      </ul>
      <button type="button" className="favorites__save"onClick={ <Link to="/src/pages/ListPage">Перейти к списку</Link>} >
        Сохранить список
      </button>

    </div>
  );
};

export default Favorites;
