import { ADD_FILM,SEARCH_QUERY } from "../constant";
export const addFilmAction=(film)=>({
    type:ADD_FILM,
    payload:film
})
export const searchQuery=(querry)=>({
    type:SEARCH_QUERY,
    payload:querry
})
