import { ADD_FILM,DELETE_FILM,LIST_NAME } from "../constant"


export const addFilmAction=(film)=>({
    type:ADD_FILM,
    payload:film
})

export const deleteFilmAction=(id)=>({
type:DELETE_FILM,
payload:id
})

export const createFavoriteList=(title)=>({
type:LIST_NAME,
payload:title
})
