import React from "react";
import { useSelector } from "react-redux";
import MovieItem from "../MovieItem/MovieItem";
import "./Movies.css";


const Movies = () => {

const films=useSelector(store=>store.filmReducer.films);
  return (
    <ul className="movies">
      {films.length > 0 && films.map((film) => (
        <li className="movies__item" key={film.imdbID}>
          <MovieItem {...film} />
        </li>
      ))}
    </ul>
  );
};

export default Movies;
