import React,{useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { createFavoriteList, deleteFilmAction } from "../../redux/addFavorite/favoriteAction";

import "./Favorites.css";

const Favorites = () => {
  const dispatch = useDispatch();

  const listName = useSelector(
    (store) => store.favoriteReducer.favoriteListName
  );
  const favoriteFilms = useSelector(
    (store) => store.favoriteReducer.favoriteFilms || []
);
const [saveBtn, setsaveBtn] = useState("Сохранить список");
const [isClicked, setisClicked] = useState(false);
  const deleteFavorite = (film) => {

    dispatch(deleteFilmAction({film}))
  };
  const updateListName = (e) => {
    const newListName=e.target.value
    dispatch(createFavoriteList(newListName));
  };
  const createFavorite = () => {
    setsaveBtn("loading...");
    setTimeout(() => {
      setisClicked(true);
    }, 300);
  };
  const enabled = listName && favoriteFilms.length > 0;


  return (
    <div className="favorites">           
      <input
        value={listName}
        className="favorites__name"
        placeholder="Новый список"
        onChange={updateListName}
        disabled={isClicked}
      />
      {favoriteFilms.length>0&&(
        <ul>
     {favoriteFilms?.map((item) => {
      return(
        <li key={item.imdbID}>
        {item.Title} ({item.Year}){" "}
        <button className="favorites__button" onClick={deleteFavorite(item)} disabled={isClicked}>X</button>
    </li>

      )

})}
</ul>
      )}
{!isClicked?(
  <button type="button" className="favorites__save" onClick={createFavorite} disabled={!enabled}>
  {saveBtn}
</button>):(<Link to="/src/pages/ListPage">Перейти к списку</Link>
)}







    </div>
  )}


export default Favorites;
