import { ADD_FILM ,DELETE_FILM} from "../constant"

const initialState={
    FavoriteFilms:[],
 favoriteListName:"",
}

const favoriteReducer=(state=initialState,action)=>{
    switch(action.type){
      case ADD_FILM:
        return{
          ...state,
          FavoriteFilms:[...state.FavoriteFilms,{...action.payload}]
        }
    default:
        return state

}}
export default filmReducer
