import {ADD_MOVIE, REMOVE_MOVIE } from "./action";
const initialState={
    movies:[]
}
export default function movieReducer(state=initialState,action){
    switch(action,type){
        case ADD_MOVIE:
            return{
                movies:[...state.movies,action.payload],
            }
        case REMOVE_MOVIE:
            return{
                movies:state.movies.filter((movie)=>movie.id!==action.payload)
            }
        default:
    }
}
