import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { createFavoriteList, deleteFilmAction } from "../../redux/addFavorite/favoriteAction";

import "./Favorites.css";

const Favorites = ({imdbID,Title,Year}) => {
  const dispatch = useDispatch();

  const listName = useSelector(
    (store) => store.favoriteReducer.favoriteListName
  );
  const favoriteFilms = useSelector(
    (store) => store.favoriteReducer.favoriteFilms 
);
const [saveBtn, setsaveBtn] = useState("Сохранить список");
const [isClicked, setisClicked] = useState(false);
  const deleteFavorite = () => {

    dispatch(deleteFilmAction({imdbID}))
  };
  const updateListName = (e) => {
    const newListName=e.target.value
    dispatch(createFavoriteList(newListName));
  };


  return (
    <div className="favorites">           
      <input
        value={listName}
        className="favorites__name"
        placeholder="Новый список"
        onChange={updateListName}
      />
     <ul>
     {favoriteFilms.map((item) => (
    <li key={item.imdbID}>
        {item.Title} ({item.Year}){" "}
        <button className="favorites__button" onClick={deleteFavorite}>X</button>
    </li>
))}
</ul>
      <button type="button" className="favorites__save" >
        Сохранить список
      </button>
      <Link to="/src/pages/ListPage">Перейти к списку</Link>
    </div>
  )}


export default Favorites;
