import React from 'react';
import './MovieItem.css';
import { useDispatch } from "react-redux";
import { addFilmAction } from '../../redux/films/action';

const MovieItem=({title,year,poster})=> {
 const dispatch=useDispatch();
 const addFilm=()=>{
    dispatch(addFilmAction({title,year}))
 }

        return (
            <article className="movie-item">
                <img className="movie-item__poster" src={poster} alt={title} />
                <div className="movie-item__info">
                    <h3 className="movie-item__title">{title}&nbsp;({year})</h3>
                    <button type="button" className="movie-item__add-button" onClick={addFilm}>Добавить в список</button>
                </div>
            </article>
        );
    }


export default MovieItem;
