import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { SEARCH_QUERY } from "../../redux/constant";
import { searchQuery } from "../../redux/films/action";
import "./SearchBox.css";

const SearchBox = () => {
  const [searchLine, setSearchLine] = useState("");
  const dispatch = useDispatch();

  const searchLineChangeHandler = (e) => {
    setSearchLine(e.target.value);
  };
  const searchBoxSubmitHandler = async (e) => {
    e.preventDefault();
    dispatch(searchQuery(searchLine));
    try {
      const response = await fetch(
        "http://www.omdbapi.com/?i=${searchline}tt3896198&apikey=226bff17"
      );
      const data = await response.json();
      if (data.Response === "True") {
        const films = data.Search.map((film) => ({
          imdbID: film.imdbID,
          title: film.Title,
          year: film.Year,
          poster: film.Poster,
        }));

      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="search-box">
      <form className="search-box__form" onSubmit={searchBoxSubmitHandler}>
        <label className="search-box__form-label">
          Искать фильм по названию:
          <input
            value={searchLine}
            type="text"
            className="search-box__form-input"
            placeholder="Например, Shawshank Redemption"
            onChange={searchLineChangeHandler}
          />
        </label>
        <button
          type="submit"
          className="search-box__form-submit"
          disabled={!searchLine}
        >
          Искать
        </button>
      </form>
    </div>
  );
};

export default SearchBox;
