import { ADD_FILM,SEARCH_QUERY } from "../constant";
export const addFilmAction=(film)=>({
    type:ADD_FILM,
    payload:film
})
export const searchQuery=(query)=>({
    type:SEARCH_QUERY,
    payload:query
})
