import { ADD_FILM,DELETE_FILM,LIST_NAME } from "../constant"


const addFilmAction=(film)=>({
    type:ADD_FILM,
    payload:film
})

const deleteFilmAction=(id)=>({
type:DELETE_FILM,
payload:id
})

const createFavoriteList=(title)=>({
type:LIST_NAME,
payload:title
})
