import React ,{useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";

import "./Favorites.css";

const Favorites = () => {
  const dispatch = useDispatch();
  const title = useSelector((store) => store.favoriteReducer.favoriteListName);
  const favoriteFilms = useSelector(
    (store) => store.favoriteReducer.favoriteFilms
  );
  const [isClicked, setisClicked] = useState(false);

  const deleteFavorite = () => {
    dispatch({type: "DELETE_FAVORITE_ITEM",
    payload:favoriteFilms ,});
  };
  const createFavoriteList = () => {

    setTimeout(() => {
      setisClicked(true);
    }, 300);
  }; 



const enable=title&&favoriteFilms
  return (
    <div className="favorites">
      <input
        value={title}
        className="favorites__name"
        onChange={(e)=>dispatch({ type: "LIST_NAME", payload: e.target.value })
      }
        placeholder="Новый список"
        disabled={isClicked}
      />
       {favoriteFilms.length > 0 &&
      <ul className="favorites__list">
        {favoriteFilms?.map((item) => {
          return (
            <li key={item.imdbID}>
              {item.title} ({item.year}){" "}
              <button className="favorites__button" onclick={()=>deleteFavorite(item)} disabled={isClicked}>X</button>
            </li>
          );
        })}
      </ul>}
      {!isClicked?(
      <button type="button"  onClick={createFavoriteList} className="favorites__save" disabled={!enable}>
        Сохранить список
      </button>):(<Link to='/src/pages/ListPage'>Перейти к списку</Link>)}
    </div>
  );
};

export default Favorites;
