import React ,{useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";

import "./Favorites.css";

const Favorites = () => {
  const dispatch = useDispatch();
  const title = useSelector((store) => store.favoriteReducer.favoriteListName);
  const favoriteFilms = useSelector(
    (store) => store.favoriteReducer.favoriteFilms
  )||[];
  const [isClicked, setisClicked] = useState(false);

  const deleteFavorite = () => {
    dispatch(deleteFilmAction());
  };

  const createFavorite = () => {
    dispatch(addFilmAction());
    setisClicked(true);
  };

  const addListName = () => {
    dispatch(addListNameAction());
  };
const enable=title&&favoriteFilms.length>0
  return (
    <div className="favorites">
      <input
        value={title}
        className="favorites__name"
        onChange={addListName}
        placeholder="Новый список"
        disabled={isClicked}
      />
       {favoriteFilms.length > 0 &&
      <ul className="favorites__list">
        {favoriteFilms.map((item) => {
          return (
            <li key={item.id}>
              {item.title} ({item.year}){" "}
              <button className="favorites__button" onclick={deleteFavorite}>X</button>
            </li>
          );
        })}
      </ul>}
      {!isClicked?(
      <button type="button"  onClick={createFavorite} className="favorites__save" disabled={!enable}>
        Сохранить список
      </button>):(<Link to='/src/pages/ListPage'>Перейти к списку</Link>)}
    </div>
  );
};

export default Favorites;
