import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { createFavoriteList, deleteFilmAction } from "../../redux/addFavorite/favoriteAction";
import MovieItem from "../MovieItem/MovieItem";
import "./Favorites.css";

const Favorites = ({id}) => {
  const dispatch = useDispatch();

  const [listName, setListName] = useState("");

  const favoriteFilms = useSelector(
    (store) => store.favoriteReducer.favoriteFilms
  );

  const deleteFavorite = () => {
    dispatch(deleteFilmAction(id));
  };
  const updateListName=(e)=>{
    setListName(e.target.value)
  }
  const createList = (e) => {
    e.preventDefault();
    dispatch(createFavoriteList(listName));
setListName("");
  };

  return (
    <div className="favorites">
      <input
        value={listName}
        className="favorites__name"
        placeholder="Новый список"
        onChange={createList}
      />
      <ul className="favorites__list">
        {favoriteFilms?.map((item) => {
          return (
            <li key={item.imdbID}>
               <MovieItem id={item.imdbID}
            title={item.Title}
           year={(item.Year)}/>
              <button className="favorites__button" onClick={deleteFavorite}>X</button>
            </li>
          );
        })}
      </ul>
      <button type="button" className="favorites__save">
        Сохранить список
      </button>
      <Link to="/src/pages/ListPage">Перейти к списку</Link>
    </div>
  );
};

export default Favorites;
