var fs2 = require('../fs')

// fs2/sync is optional module and its dependency - synchronize.js not included in
// fs2 dependency modules list, You should install it by itself.
var sync = require('synchronize')

sync(fs2,
  'createDirectory',
  'delete',
  'deleteDirectory',
  'writeFile',
  'writeFileAtomically',
  'exists',
  'isFile',
  'isDirectory',
  'readDirectory',
  'copy',

  'readFile',
  'writeFile',
  'stat',

  'getTemporarryDirectory'
  )

module.exports = fs2